import Pad from "./Pad";
import React from "react";
export default function Pads(props) {
  const hihats = [
    { label: "hihat-acoustic", value: "hihatAcoustic", type: "hihat" },
    { label: "hihat-chick", value: "hihatChick", type: "hihat" },
    { label: "hihat-closed", value: "hihatClosed", type: "hihat" },
    { label: "hihat-open", value: "hihatOpen", type: "hihat" },
  ];
  const snares = [
    { label: "snare-acoustic", value: "snareAcoustic", type: "snare" },
    { label: "snare-power", value: "snarePower", type: "snare" },
    { label: "snare-rim", value: "snareRim", type: "snare" },
    { label: "snare-rock", value: "snareRock", type: "snare" },
  ];
  const kicks = [
    { label: "kick-clean", value: "kickClean", type: "kick" },
    { label: "kick-house", value: "kickHouse", type: "kick" },
    { label: "kick-rifle", value: "kickRifle", type: "kick" },
    { label: "kick-synth", value: "kickSynth", type: "kick" },
    { label: "kick-sweden", value: "kickSweden", type: "kick" },
  ];
  // turns on / off the pads
  function toggle(id) {
    props.setPadsData((prevArray) =>
      prevArray.map((item) =>
        item.id === id ? { ...item, on: !item.on } : item
      )
    );
  }

  const padsMapped = props.padsData.map((pad) => {
    return (
      <Pad
        key={pad.id}
        on={pad.on}
        color={pad.color}
        id={pad.id}
        type={pad.type}
        scale={pad.scale}
        toggle={toggle}
        activate={props.activate}
      />
    );
  });

  const hihatsMapped = hihats.map((hihat) => {
    return <option value={hihat.value}>{hihat.label}</option>;
  });

  const snaresMapped = snares.map((snare) => {
    return <option value={snare.value}>{snare.label}</option>;
  });

  const kicksMapped = kicks.map((kick) => {
    return <option value={kick.value}>{kick.label}</option>;
  });

  function submitSound(e) {
    console.log(e.target.value);
  }

  /*   React.useEffect(() => {
    console.log(props.soundChoices);
  }, [props.soundChoices]); */

  return (
    <section className="pad-area">
      {/** drop-down menus */}
      <form className="pads-description">
        <div className="dropdown-wrapper">
          <select name="hihat" onChange={submitSound}>
            {hihatsMapped}
          </select>
        </div>
        <div className="dropdown-wrapper">
          <select name="snare" onChange={submitSound}>
            {snaresMapped}
          </select>
        </div>
        <div className="dropdown-wrapper">
          <select name="kick" onChange={submitSound}>
            {kicksMapped}
          </select>
        </div>
      </form>
      {/** actual pads */}
      <div className="pads-container">{padsMapped}</div>
    </section>
  );
}
