import Pads from "./Pads";
import Tasks from "./Tasks";
import React from "react";
import { pads } from "../data";

export default function Home() {
  // vars for Tasks.jsx
  const [startText, setStartText] = React.useState(false);
  const [sliderVolume, setSliderVolume] = React.useState(30);

  // vars for Pads.jssx

  // --- FUNCTIONS FOR TASKS.JSX
  function toggleStart(e) {
    e.preventDefault();

    // grabbing the volume (deafault at 30)
    const formData = sliderVolume;

    // change "start" to "pause" or vise versa
    setStartText((prev) => !prev);

    console.log(formData);
  }

  // --- FUNCTIONS FOR PADS.JSX
  function makeSound(index){
    // deciding the delay before making a sound, based on the pad's position (index)
    var timeOut = 250; // 2000 milSec / 8 columns = 250 milsec / column
    if([1,9,17].includes(index)){
      timeOut *= 0; // column 1
    } else if ([2,10,18].includes(index)){
      timeOut *= 1; // column 2
    }else if ([3,11,19].includes(index)){
      timeOut *= 2; // column 3
    }else if ([4,12,20].includes(index)){
      timeOut *= 3; // column 4
    }else if ([5,13,21].includes(index)){
      timeOut *= 4; // column 5
    }else if ([6,14,22].includes(index)){
      timeOut *= 5; // column 6
    }else if ([7,15,23].includes(index)){
      timeOut *= 6; // column 7
    }else if ([8,16,24].includes(index)){
      timeOut *= 7; // column 8
    }

    // actual function
    setTimeout()
  } 

  return (
    <div className="web-body">
      <section className="action-area">
        <Pads 
          makeSound = {makeSound}
        />
      </section>
      <section className="task-area">
        <Tasks 
        toggleStart={toggleStart}
        sliderVolume = {sliderVolume}
        setSliderVolume = {setSliderVolume}
        startText = {startText}
         />
      </section>
    </div>
  );
}
