import React from "react"

export default function Pad(props){
    const [on, setOn] = React.useState(props.on)
    const styles = {
        backgroundColor: on? props.color:"white"
    }

    return(
        <button 
            className="pad" 
            style={styles}
            onClick={()=>props.toggle(props.id)}
        />
            
       
    )
}
