import Pads from "./Pads";
import Tasks from "./Tasks";
import React from "react";
import { pads } from "../data";

export default function Home() {
  // vars for Tasks.jsx
  const [isStart, setIsStart] = React.useState(false);
  const [tempo, setTempo] = React.useState(30);
 

  // vars for Pads.jssx
  const [padsData, setPadsData] = React.useState(pads);

  // --- FUNCTIONS FOR TASKS.JSX

  function activate(pad){
    // scaling the pad
    if(pad.on){
      console.log(pad.id)
    }
  }

  function startPlaying(padsData){
    for (let pad of padsData){
      if(isStart){
        activate(pad)
      }
    }
  }

  function toggleStart(e) {
    e.preventDefault();

    // 1. grabbing the Tempo (deafault at 30)
    const formData = tempo;

    // 2. change "start" to "pause" or vise versa
    setIsStart((prev) => !prev);
    
  }

  // ---------------- testing --------------


  const array = [1,2,3,4]

  function myLoop(){
    setTimeout(()=>{console.log},500)
  }

  // ----------------------------------------
  


  return (
    <div className="web-body">
      <section className="action-area">
        <Pads
          padsData={padsData}
          setPadsData={setPadsData}
          isStart={isStart}
        />
      </section>
      <section className="task-area">
        <Tasks
          padsData={padsData}
          toggleStart={toggleStart}
          tempo={tempo}
          setTempo={setTempo}
          isStart={isStart}

        />
      </section>
    </div>
  );
}
