export default function Tasks(props) {
  const keyboardOptions = ["bass", "piano"];
  const keyboardOptionsMapped = keyboardOptions.map((opt) => {
    return <option className="dropdown-option">{opt}</option>;
  });
  function toggleDropdown() {
    // when the dropdown menu is clicked, show the key positions on the keyboard

    props.setOpenKeyPositions((prev) => !prev);
  }

  function submitDropdown(e) {
    props.setOpenKeyPositions((prev) => !prev);
    props.setKeyboardChoice(e.target.value);
  }

  function toggleKeyPositions(){
    props.setOpenKeyPositions((prev) => !prev)
  }

  return (
    <section className="tasks-container">
      <div className="form">
        <form
          className="keyboard-form"
          onClick={toggleDropdown}
        >
          <select onClick={toggleKeyPositions}>{keyboardOptionsMapped}</select>
        </form>
      </div>
      <div className="rest">
        <form className="slider-form" onSubmit={(e) => props.toggleStart(e)}>
          <button className="big-button" type="submit">
            {props.isStart ? "Pause" : "Start"}
          </button>
          <input
            name="slider"
            type="range"
            min="0"
            max="100"
            value={props.tempo}
            onChange={(e) => props.setTempo(e.target.value)}
            className="slider"
          ></input>
        </form>
        <button className="small-button">Record</button>
        <button className="small-button">My Beats</button>
      </div>
    </section>
  );
}
