export default function Pad(props){
    const styles = {
        backgroundColor: props.color,
        opacity: props.on? 1:0.1
    }

    props.makeSound(props.id)

    return(
        <button 
            className="pad" 
            style={styles}
            onClick={()=>props.toggle(props.id)}
            
        />
            
       
    )
}
