import React from "react";
import { Link } from "react-router-dom";

export default function Tasks() {
  const [sliderVolume, setSliderVolume] = React.useState(30);
  const [startText, setStartText] = React.useState(false)
  function handleSubmit(e) {
    e.preventDefault();
    
    const formData = sliderVolume
    // change "start" to "pause" or vise versa
    setStartText(prev=>!prev)
    console.log(formData);
  }
  return (
    <section className="tasks-container">
      <form className="slider-form" onSubmit={(e) => handleSubmit(e)}>
        <button className="big-button" type="submit">
          {startText? "Pause" : "Start"}
        </button>
        <input
          name="slider"
          type="range"
          min="0"
          max="100"
          value={sliderVolume}
          onChange={(e) => setSliderVolume(e.target.value)}
          className="slider"
          
        ></input>
      </form>
      <button className="small-button">Record</button>
      <button className="small-button">My Beats</button>
    </section>
  );
}
